/* When the socket connects, it outputs the socket id. */
socket.on('connect', () => {
    console.log(`Connected with id: ${socket.id}`)
})

/* Event that shows the command that is sent to the Translate Module. */
socket.on('relaycommand', (command) => {
    console.log(`Sending command: ${command}`)
})

/* Event that shows the data received from the Translate Module. */
socket.on('relaydata', (data) => {
    console.log(`Received data: ${data}`)
})

/* Event that occurs when there is an error in the connection. Logs the error message to the console. */
socket.on('connect_error', (err) => {
    console.log(err.message);
})

/* Event that occurs when their is a disconnect.*/
socket.on("disconnect", (reason) => {
    console.log(reason)
    /* If it was the server that initiated the disconnect. */
    if (reason === "io server disconnect") {
        socket.connect() // Manual reconnection.
    }

    // If it was not the server that disconnected, then automatically reconnect.
})
