import React from 'react'
import { io } from 'socket.io-client';  

const socket = io('http://192.168.0.24:5000/')

// DELETE TEST BLOCK 0
var message = {value: ''};
var inputComp = {comp: null}

function handleChange(e) {
    message.value = e.target.value;
    inputComp.comp = e.target;
}
// DELETE TEST BLOCK 0

export default function App() {
    console.log(socket)

    return (
        // DELETE TEST BLOCK 1
        <div>
            <input type='text' onChange={(e) => handleChange(e)}/>

            <button onClick={relayMessage}>
                Send message.
            </button>
        </div>
        // DELETE TEST BLOCK 1
    )
}

// DELETE TEST BLOCK 2
function relayMessage() {
    if (socket != null) {
        console.log(message.value);
        socket.emit('relaymessage', message.value);
    }

    message.value = '';
    inputComp.comp.value = '';
}

socket.on('relaymessage', () => {

})
// DELETE TEST BLOCK 2

socket.on('connect', () => {
    console.log(`Connected with id: ${socket.id}`)
})

socket.on('relaycommand', (command) => {
    console.log(`Relaying command: ${command}`)
})

socket.on('connect_error', (err) => {
    console.log(err.message);
})

socket.on("disconnect", (reason) => {
    console.log(reason)
    if (reason === "io server disconnect") {
        // the disconnection was initiated by the server, you need to reconnect manually
        socket.connect()
    }
    // else the socket will automatically try to reconnect
})
